(window.webpackJsonp=window.webpackJsonp||[]).push([[601],{"./src/Assessment/Statistics/Statistics.html":function(s,a){s.exports='\n<div class="dash-subhead">\n  <translate>STATISTICS</translate>\n</div>\n\n<div flex layout="column" class="dash-stat-column" layout="column" layout-align="start">\n\n  <md-card md-whiteframe="2" class="dash-stat-card"\n           ng-show="vm.projectData.isMember || vm.projectData.isViewer"\n           ng-class="vm.projectData.isMember ? \'longer\' : \'shorter\'">\n\n    <md-icon ng-show="vm.projectData.isPublished" class="dash-trendicon rotate">rotate_right</md-icon>\n    <md-icon ng-hide="vm.projectData.isPublished" class="dash-trendicon rotate">assignment</md-icon>\n    <span class="dash-stat-nr dash-stat-nr-version" ng-show="vm.projectData.isPublished">{{vm.currentVersion}}.</span>\n    <span class="dash-stat-nr draft-version" ng-hide="vm.projectData.isPublished">\n      <translate>Draft</translate>\n    </span>\n    <span class="dash-stat-label">\n      <translate>Version</translate>\n    </span>\n\n    <div class="gray" ng-show="vm.projectData.isMember && vm.projectData.isPublished" layout="column" layout-align="space-around stretch">\n      <span class="desc">\n        <translate>Push to save your current stats as a new version:</translate>\n      </span>\n      <md-button class="snapshot-button md-whiteframe-1dp" ng-click="vm.snapShot()">\n        <span class="text plus">\n          +\n        </span>\n        <span class="text">\n          <translate>snapshot</translate>\n        </span>\n      </md-button>\n    </div>\n    <div class="gray" ng-show="vm.projectData.isMember && !vm.projectData.isPublished" layout="column" layout-align="space-around stretch">\n      <span class="desc">\n        <translate>Go to the edit project page to review and publish your project</translate>\n      </span>\n      <md-button class="snapshot-button draft md-whiteframe-1dp" ng-click="vm.goToEditProject()">\n        <span class="text">\n          <translate>Review &amp;<br> Publish</translate>\n        </span>\n      </md-button>\n    </div>\n\n  </md-card>\n\n\n  <donors class="hten" number="vm.projectData.donors.length"></donors>\n\n</div>\n'}}]);