(window.webpackJsonp=window.webpackJsonp||[]).push([[570],{"./src/Cms/ExperiencesList/ExperiencesList.html":function(e,n){e.exports='<div md-whiteframe="2" class="cms-experience-list" layout="row" layout-align="start stretch">\n  <div class="experiences-list" flex="40">\n    <h3 class="md-title">\n      <translate>Learn from others\' experiences</translate>\n    </h3>\n    <p class="empty-card" ng-show="vm.experiences.length === 0">\n      <translate>No experience has been added yet...</translate>\n    </p>\n    <cms-list-element item="item" ng-repeat="item in vm.experiences | orderBy:\'id\':true | limitTo:2"></cms-list-element>\n    <md-button ui-sref="cms({\'#\': \'experiences\'})" class="md-primary md-raised">\n      <translate>Show more</translate>\n    </md-button>\n  </div>\n\n  <div class="add-experience" flex="60">\n    <h3 class="md-title">\n      <translate>Share your own experience</translate>\n    </h3>\n    <form name="vm.form">\n      <div layout="row">\n\n        <md-input-container>\n          <label>\n            <translate>What is your experience about (title)</translate>\n          </label>\n          <input type="text" ng-model="vm.newExperience.name"  name="name" required md-maxlength="120" />\n          <div ng-messages="vm.form.name.$error">\n            <div ng-message="required">\n              <translate>This is required.</translate>\n            </div>\n            <div ng-message="md-maxlength">\n              <translate>The name has to be less than 120 characters long.</translate>\n            </div>\n          </div>\n        </md-input-container>\n\n      </div>\n    </form>\n    <trix-component placeholder="{{\'Add new experience\' | translate }}" value="vm.newExperience.body" valid="vm.newExperience.valid" char-limit="2500"></trix-component>\n    <md-button class="md-primary md-raised" ng-disabled="vm.disableAddButton(vm.newExperience)" ng-click="vm.saveExperience()">\n      <translate>Add experience</translate>\n    </md-button>\n  </div>\n</div>\n'}}]);