(window.webpackJsonp=window.webpackJsonp||[]).push([[168],{"./src/Project/CountryFields/CountryFields.html":function(n,e){n.exports='<div class="project-section section-country-fields" md-whiteframe="2" ng-show="vm.countryFields && vm.countryFields.length > 0">\n\n  <div class="section-title md-title">\n    <translate>5. Country fields</translate>\n    <span class="toggler" ng-click="vm.collapse()"><i class="material-icons">keyboard_arrow_down</i></span>\n  </div>\n\n  <div class="section-wrapper">\n\n    <div class="generic-container" flex ng-repeat="field in vm.countryFields">\n\n      \x3c!-- TEXT INPUT--\x3e\n      <div ng-if="field.type === 1">\n        <h6>{{field.question}}</h6>\n        <md-input-container\n          class="md-block string-field">\n            <textarea\n              aria-label="{{field.id}}"\n              ng-required="vm.activateValidation && field.required"\n              ng-blur="vm.updateProjectCountryFields(field)"\n              md-maxlength="1000"\n              rows="1"\n              name="custom{{$index}}"\n              ng-model="field.answer"\n              md-select-on-focus></textarea>\n          <div ng-messages="vm.form[\'custom\' + $index].$error">\n            <div ng-message="required">\n              <translate>This is required.</translate>\n            </div>\n          </div>\n        </md-input-container>\n      </div>\n\n\n      \x3c!-- NUMERIC INPUT--\x3e\n      <div ng-if="field.type === 2">\n        <h6>{{field.question}}</h6>\n        <md-input-container\n          class="md-block number-field">\n          <input type="number"\n                 aria-label="{{field.id}}"\n                 ng-required="vm.activateValidation && field.required"\n                 ng-blur="vm.updateProjectCountryFields(field)"\n                 name="custom{{$index}}"\n                 ng-model="field.answer">\n          <div ng-messages="vm.form[\'custom\' + $index].$error">\n            <div ng-message="required">\n              <translate>This is required.</translate>\n            </div>\n          </div>\n        </md-input-container>\n      </div>\n\n\n      \x3c!-- RADIO BUTTON--\x3e\n      <div\n        ng-if="field.type === 3"\n        class="yesno-field">\n        <h6>{{field.question}}</h6>\n        <md-radio-group\n          ng-model="field.answer"\n          aria-label="{{field.id}}"\n          ng-blur="vm.updateProjectCountryFields(field)"\n          name="custom{{$index}}"\n          md-no-ink\n          ng-required="vm.activateValidation && field.required"\n          class="md-primary"\n          layout="row">\n          <md-radio-button ng-value="true" aria-label="custom{{$index}}-yes">\n            <translate>Yes</translate>\n          </md-radio-button>\n          <md-radio-button ng-value="false" aria-label="custom{{$index}}-no">\n            <translate>No</translate>\n          </md-radio-button>\n        </md-radio-group>\n        <div class="radio-error-messages">\n          <div ng-messages="vm.form[\'custom\' + $index].$error">\n            <div ng-message="required">\n              <translate>This is required.</translate>\n            </div>\n          </div>\n        </div>\n      </div>\n\n\n      \x3c!--SINGLE SELECTION--\x3e\n      <div\n        ng-if="field.type === 4"\n        class="single-selection-field">\n        <h6>{{field.question}}</h6>\n        <md-input-container class="md-block">\n          <md-select ng-model="field.answer"\n                     aria-label="{{field.id}}"\n                     name="custom{{$index}}"\n                     aria-label="custom{{$index}}"\n                     ng-required="vm.activateValidation && field.required"\n                     md-on-close="vm.updateProjectCountryFields(field)">\n            <md-option value="{{option}}" ng-repeat="option in field.options" >\n              {{option}}\n            </md-option>\n          </md-select>\n          <div class="radio-error-messages">\n            <div ng-messages="vm.form[\'custom\' + $index].$error">\n              <div ng-message="required">\n                <translate>This is required.</translate>\n              </div>\n            </div>\n          </div>\n        </md-input-container>\n      </div>\n\n\n      \x3c!--MULTIPLE SELECTION--\x3e\n      <div\n        ng-if="field.type === 5"\n        class="multi-selection-field">\n        <h6>{{field.question}}</h6>\n        <md-input-container class="md-block">\n          <md-select ng-model="field.answer"\n                     aria-label="{{field.id}}"\n                     md-select-separator="empty"\n                     aria-label="custom{{$index}}"\n                     multiple\n                     name="custom{{$index}}"\n                     ng-required="vm.activateValidation && field.required"\n                     md-on-close="vm.updateProjectCountryFields(field)">\n            <md-option value="{{option}}" ng-repeat="option in field.options">\n              {{option}}\n            </md-option>\n          </md-select>\n          <div class="radio-error-messages">\n            <div ng-messages="vm.form[\'custom\' + $index].$error">\n              <div ng-message="required">\n                <translate>This is required.</translate>\n              </div>\n            </div>\n          </div>\n        </md-input-container>\n      </div>\n\n    </div>\n\n  </div>\n\n</div>\n'}}]);