(window.webpackJsonp=window.webpackJsonp||[]).push([[597],{"./src/Cms/AddNewContent/AddNewContentDialog.html":function(n,e){n.exports='<md-dialog aria-label="add-new-content" class="detail-dialog new-content-dialog">\n  <md-toolbar>\n    <div class="md-toolbar-tools">\n      <h3 class="md-title" ng-hide="vm.newContent.id">\n        <translate>Add new</translate>\n        <span>\n          <translate>content</translate>\n        </span>\n      </h3>\n      <h3 class="md-title" ng-show="vm.newContent.id">Edit content</h3>\n      <span flex></span>\n      <md-button class="md-icon-button" ng-click="vm.cancel()">\n        <md-icon  aria-label="Close dialog">close</md-icon>\n      </md-button>\n    </div>\n  </md-toolbar>\n\n  <form name="vm.form" ng-submit="vm.submit($event)" novalidate>\n    <md-dialog-content>\n      <div class="md-dialog-content">\n\n        <div class="content">\n          <div layout="column">\n            <md-input-container >\n              <label>\n                <translate>Select a type</translate>\n              </label>\n              <md-select ng-model="vm.newContent.type" required name="type" >\n                <md-option ng-value="1">\n                  <translate>Resources</translate>\n                </md-option>\n                <md-option ng-value="2" ng-if="vm.isSuperUser" >\n                  <translate>Tips &amp; Considerations</translate>\n                </md-option>\n                <md-option ng-value="3" >\n                  <translate>Experiences</translate>\n                </md-option>\n              </md-select>\n              <div ng-messages="vm.form.type.$error">\n                <div ng-message="required">\n                  <translate>This is required.</translate>\n                </div>\n              </div>\n            </md-input-container>\n            <md-input-container>\n              <label>\n                <translate>Select a domain</translate>\n              </label>\n              <md-select aria-label="domain-selector" name="domain" ng-model="vm.newContent.domain" required class="">\n                <md-optgroup label="{{axis.name}}" ng-repeat="axis in vm.axes">\n                  <md-option ng-value="domain.id" ng-repeat="domain in axis.domains track by domain.id">{{domain.name}}</md-option>\n                </md-optgroup>\n              </md-select>\n              <div ng-messages="vm.form.domain.$error">\n                <div ng-message="required">\n                  <translate>This is required.</translate>\n                </div>\n              </div>\n            </md-input-container>\n\n            <md-input-container>\n              <label>\n                <translate>Add a title</translate>\n              </label>\n              <input type="text" ng-model="vm.newContent.name"  name="name" required md-maxlength="120" />\n              <div ng-messages="vm.form.name.$error">\n                <div ng-message="required">\n                  <translate>This is required.</translate>\n                </div>\n                <div ng-message="md-maxlength">\n                  <translate>The name has to be less than 120 characters long.</translate>\n                </div>\n              </div>\n            </md-input-container>\n          </div>\n\n          <trix-component value="vm.newContent.body" show-error="vm.showTrixError" valid="vm.newContent.textValid" placeholder="{{\'Add content\' | translate}}" char-limit="2500" required></trix-component>\n\n          <div class="file-upload" layout="row" layout-align="start center" ng-if="vm.newContent.type !== 3">\n            <div class="selected-file-thumbnail">\n              <img ngf-thumbnail="vm.newContent.cover" ngf-size="{width: auto, height: 36, quality: 0.9}">\n            </div>\n            <div class="md-button md-gray" ngf-select ng-model="vm.newContent.cover" ngf-pattern="\'image/*\'"\n                 ngf-accept="\'image/*\'" ngf-max-size="10MB" ngf-min-height="100" ngf-before-model-change="vm.beforeImageSelect()" ng-change="vm.imageSelected()">\n              <md-icon>insert_photo</md-icon>\n              <translate>Add cover image</translate>\n            </div>\n            <div>\n              <small>\n                <translate>Maximum file size: 10MB</translate>\n              </small>\n            </div>\n          </div>\n        </div>\n      </div>\n    </md-dialog-content>\n\n    <md-dialog-actions layout="row">\n      <md-button ng-click="vm.cancel()">\n        <translate>Cancel</translate>\n      </md-button>\n      <span flex></span>\n      <md-button  class="md-primary md-raised" ng-disabled="vm.disableSubmit" type="submit">\n        <translate>Submit</translate>\n      </md-button>\n    </md-dialog-actions>\n  </form>\n\n</md-dialog>\n'}}]);