(window.webpackJsonp=window.webpackJsonp||[]).push([[499],{"./src/Common/Reset/Reset.html":function(n,e){n.exports='<div class="reset-wrapper" layout="column" layout-align="start center" ng-style="vm.style">\n    <div class="page-title">\n        <h1 class="md-display-1 text-center"><translate>Reset password</translate></h1>\n    </div>\n\n    <div md-whiteframe="2">\n        <md-content layout="column">\n            <form layout="column" name="vm.resetForm" ng-submit="vm.reset()" ng-hide="vm.sent" novalidate>\n                <div id="title-text">\n                    <translate>Enter your email address and follow the instructions you will get by email.</translate>\n                </div>\n                <md-input-container>\n                    <label><translate>Email address</translate></label>\n                    <input ng-required="true" name="email" ng-model="vm.email" type="email" ng-change="vm.handleCustomError(\'email\')" />\n                    <div ng-messages="vm.resetForm.email.$error">\n                        <div ng-message="required"><translate>This is required.</translate></div>\n                        <div ng-message="email"><translate>This field must be a valid email.</translate></div>\n                        <div ng-message="custom" ng-repeat="error in vm.resetForm.email.customError">{{error}} </div>\n                    </div>\n                </md-input-container>\n\n                <p class="global-error">\n                    <span ng-repeat="error in vm.resetForm.non_field_errors"> {{error}}</span>\n                </p>\n\n                <div layout="row" layout-align="start">\n                    <div flex=auto>\n                        <md-button class="md-gray md-small" ui-sref="login">\n                            <translate>Go back to login</translate>\n                        </md-button>\n                    </div>\n                    <div flex=grow layout-align="end">\n                        <md-button type="submit" class="md-raised md-primary">\n                            <translate>Reset</translate>\n                        </md-button>\n                    </div>\n                </div>\n            </form>\n            <div layout="column" class="form-success" ng-show="vm.sent">\n                <i class="material-icons">check_circle</i>\n                <h4 class="text-center"><translate>Congratulations!</translate></h4>\n                <p class="text-center">\n                    <translate>An email with instructions to reset your password have been sent.</translate>\n                </p>\n                <div layout="row" layout-align="center">\n                    <div>\n                        <md-button class="md-raised md-primary" ui-sref="login">\n                            <translate>Go back to login</translate>\n                        </md-button>\n                    </div>\n                </div>\n            </div>\n        </md-content>\n    </div>\n\n    <disclaimer></disclaimer>\n</div>\n'}}]);